import { createStore } from "redux";

const add = document.getElementById("add");
const minus  = document.getElementById("minus");
const number = document.querySelector("span");

//! 중요: A reducer is a function that modifies your data. 
const reducer = () => {
  //! 중요: 리턴값은 곧 (저장될) 데이터가 된다.
  return "whatever reducer returns, that will be my data."
}; 

//! Only countModifier can modifies the state ( in this, the state is "count" )
const countModifier = (count = 0, action) => { //! An action is a way to communicate with the reducer ( the countModifier )
  if (action.type === "ADD") {
    return count + 1;
  }
  return count;
}; 

const countStore = createStore(countModifier);

//! To communicate with countModifier, Send a message by using "dispatch"
countStore.dispatch({type: "ADD"}); //! message should be an OBJECT!, That is a rule.
